package leetcode;

import org.junit.jupiter.params.ParameterizedTest;
import org.junit.jupiter.params.provider.Arguments;
import org.junit.jupiter.params.provider.MethodSource;

import java.util.stream.Stream;

import static org.junit.jupiter.api.Assertions.*;

class PermutationInStringTest {
    @ParameterizedTest
    @MethodSource(value = "generateSource")
    void test(String s1, String s2, boolean result) {
        assertEquals(result, new PermutationInString().checkInclusion(s1, s2));
    }

    private static Stream<Arguments> generateSource() {
        return Stream.of(
                Arguments.of(
                        "",
                        "",
                        true),
                Arguments.of("adc", "dcda", true),
                Arguments.of("ab", "eidbaooo", true),
                Arguments.of("ab", "eidboaoo", false));
    }
}